<?php

namespace PayU\ApplePay\Decoding;

use phpseclib\File\ASN1;

class Asn1WrapperTest extends \PHPUnit_Framework_TestCase
{
    /** @var Asn1Wrapper */
    private $asn1Wrapper;

    /**
     * @var string - base64 encoded pkcs7 certificate
     */
    private $certificateToTest = '';

    public function setUp()
    {
        $asn1 = new ASN1();
        $this->asn1Wrapper =  new Asn1Wrapper($asn1);
        $this->asn1Wrapper->loadFromString(base64_decode($this->certificateToTest));
    }

    public function testParsingOK()
    {
        $expectedSigningTime = 'Mon, 11 Dec 2017 16:10:25 +0000';
        $expectedSignature = 'MEUCIEZvNK+I5N/EE6yYCHJqijamwaHHhW9pQAlsCSFocosWAiEAmzl1jc20RxbfVtiD1Z7C5u2UtmKCDHO2s5Eab0fnyys=';
        $expectedSignedAttributes = 'MYGVMBgGCSqGSIb3DQEJAzELBgkqhkiG9w0BBwEwHAYJKoZIhvcNAQkFMQ8XDTE3MTIxMTE2MTAyNVowKgYJKoZIhvcNAQk0MR0wGzANBglghkgBZQMEAgEFAKEKBggqhkjOPQQDAjAvBgkqhkiG9w0BCQQxIgQgwsYUbK8j9xu7zed2B5jbOYSNaenOmC5cf1ZV01+DHOY=';
        $expectedDigestMessage = 'wsYUbK8j9xu7zed2B5jbOYSNaenOmC5cf1ZV01+DHOY=';
        $expectedLeafPublicKey = 'MFkwEwYHKoZIzj0CAQYIKoZIzj0DAQcDQgAEgjD9q8Oc914gLFDZm0US5jfiqQHdbLPgsc1LUmeY+M9OvegaJajCHkwz3c6OKpbC9q+hkwNFxOh6RCbOlRsSlQ==';

        $this->assertEquals($expectedSigningTime, $this->asn1Wrapper->getSigningTime());
        $this->assertEquals(base64_decode($expectedSignature), $this->asn1Wrapper->getSignature());
        $this->assertEquals(base64_decode($expectedSignedAttributes), $this->asn1Wrapper->getSignedAttributes());
        $this->assertEquals(base64_decode($expectedDigestMessage), $this->asn1Wrapper->getDigestMessage());
        $this->assertEquals(base64_decode($expectedLeafPublicKey), $this->asn1Wrapper->getLeafCertificatePublicKey());

    }
}
